const months = [
  "января", "февраля", "марта", "апреля", "мая", "июня",
  "июля", "августа", "сентября", "октября", "ноября", "декабря"
];

for (const item of items) {
  // Получаем данные из предыдущего узла "Set".
  // Важно: обращение к полям с пробелами в названии происходит через item.json["Имя Поля"]
  const sender = item.json.Отправитель;
  const subject = item.json.Тема;
  const rawMessageBody = item.json.Сообщение || ""; // Сырой текст письма, если его нет, то пустая строка
  const receivedDateStr = item.json["Дата Получения"]; // Правильное обращение к полю "Дата Получения"

  // --- Блок форматирования даты и времени ---
  let formattedDate = "Ошибка: Дата не найдена"; // Значение по умолчанию, если дату не удалось обработать

  if (!receivedDateStr) {
    // Если поле "Дата Получения" пустое или отсутствует
    console.error("Ошибка: Поле 'Дата Получения' не найдено или пустое для элемента:", item.json);
  } else {
    const dateObj = new Date(receivedDateStr);

    // Проверяем, является ли полученный объект даты корректным (не "Invalid Date")
    if (isNaN(dateObj.getTime())) {
      console.error("Ошибка: Неверный формат даты в поле 'Дата Получения':", receivedDateStr);
    } else {
      // Извлекаем компоненты даты и времени и форматируем их
      const day = String(dateObj.getDate()).padStart(2, '0'); // День с ведущим нулем
      const monthIndex = dateObj.getMonth(); // Индекс месяца (0-11)
      const year = dateObj.getFullYear(); // Полный год
      const hours = String(dateObj.getHours()).padStart(2, '0'); // Часы с ведущим нулем
      const minutes = String(dateObj.getMinutes()).padStart(2, '0'); // Минуты с ведущим нулем

      // Собираем отформатированную строку
      formattedDate = `${day} ${months[monthIndex]} ${year} ${hours}:${minutes}`;
    }
  }
  // Добавляем отформатированную дату в новый ключ "ФорматированнаяДата"
  item.json.ФорматированнаяДата = formattedDate;
  // --- Конец блока форматирования даты и времени ---


  // --- Блок очистки текста сообщения ---
  let cleanedMessage = rawMessageBody;

  // 1. Удаляем HTML-теги (например, <div>, <p>, <a> и т.д.)
  // Это помогает избавиться от HTML-структуры, если письмо пришло в таком виде.
  cleanedMessage = cleanedMessage.replace(/<[^>]*>/g, '');

  // 2. Декодируем основные HTML-сущности (например, &amp; становится &, &lt; становится <)
  // Это нужно, чтобы специальные символы отображались корректно.
  cleanedMessage = cleanedMessage.replace(/&amp;/g, '&')
                               .replace(/&lt;/g, '<')
                               .replace(/&gt;/g, '>')
                               .replace(/&quot;/g, '"')
                               .replace(/&#39;/g, "'")
                               .replace(/&nbsp;/g, ' '); // Также заменим неразрывные пробелы

  // 3. Удаляем URL-адреса.
  // Это регулярные выражения для удаления ссылок, начинающихся с http/https/ftp или www.
  // Они пытаются удалить ссылки, но могут быть не идеальны для всех случаев.
  cleanedMessage = cleanedMessage.replace(/(?:https?|ftp):\/\/[\n\S]+/g, '');
  cleanedMessage = cleanedMessage.replace(/www\.[^\s]+/g, '');

  // 4. Заменяем множественные переносы строк на один пробел и схлопываем множественные пробелы в один.
  // В конце обрезаем пробелы с начала и конца строки (trim()).
  cleanedMessage = cleanedMessage.replace(/(\r\n|\n|\r)/gm, ' '); // Все виды переносов строк заменяем на пробел
  cleanedMessage = cleanedMessage.replace(/\s+/g, ' ').trim();    // Схлопываем множественные пробелы и удаляем по краям

  // Добавляем очищенное сообщение в новый ключ "ОчищенноеСообщение"
  item.json.ОчищенноеСообщение = cleanedMessage;
  // --- Конец блока очистки сообщения ---
}

// Возвращаем измененные данные для следующего узла в workflow.
return items;
